jQuery(document).ready(function(){new WOW().init();var d=document.querySelectorAll('[href*="#"]');for(var c=0,a=d.length;c<a;c++){var b=new RegExp(window.location.hostname+window.location.pathname);if(!b.test(d[c].href)){continue}d[c].setAttribute("data-scroll",true)}if(jQuery("#g-navigation").css("position")==="fixed"){jQuery(window).scroll(function(){if(jQuery(this).scrollTop()>200){jQuery("#g-navigation").fadeIn(500)}else{jQuery("#g-navigation").fadeOut(500)}})}});